import React, { Component, Fragment } from 'react';
import styled from 'styled-components'

const AppTitle = styled.h3`
  color: #ffffff;
  opacity: 1;
  height: auto;
  margin-top: 120px;
  font-size: 50px;
  text-align: center;
  transition: all .3s ease-in-out;

  &:hover {
    transform: scale(1.1);
  }
`
const InputWrapper = styled.div`
  text-align: center;
  margin-top: 60px;
  height: 50px;
`
const SearchInput = styled.input`
  font-size: 18px;
  width: 250px;
  border-radius: 5px;
  border: none;
  height: 50px;
  margin-right: 10px;
  &:focus {
    outline: none;
  }
  &::placeholder {
    font-size: 18px;
    height: 50px;
    line-height: 50px;
  }
`
const SearchButton = styled.button`
  height: 50px;
  width: 100px;
  line-height: 50px;
  border: none;
  border-radius: 5px;
  text-align: center;
  font-size: 18px;
  cursor: pointer;
  &:hover {
    opacity: 0.8;
  }
`
const OutputWrapper = styled.div`
  height: 800px;
  width: 500px;
  margin: 80px auto;
  font-size: 20px;
  color: #ffffff;
  text-align: center;
  background-color: rgba(0,0,0,0.4);
`
const CityName = styled.p`
  font-size: 30px;
  font-weight: 800;
`
const IconWrapper = styled.img`
  width: 200px;
  height: 200px;
`

class App extends Component {
  state = {
    inputValue: '',
    weatherInfo: ''
  }

  inputChange = (e) => {
    this.setState({
      inputValue: e.target.value
    })
  }

  searchWeather = () => {
    const value = this.state.inputValue
    const APIkey = '638d3030eafa245040554df387be8a9c'
    const source = `https://api.openweathermap.org/data/2.5/weather?q=${value}&appid=${APIkey}`
    fetch(source)
      .then(response => response.json())
      .then(weatherData => {
        const iconId = weatherData.weather[0].icon
        const iconURL = `http://openweathermap.org/img/wn/${iconId}@2x.png`
        console.log(weatherData)
        const weatherInfo = {
          cityName: weatherData.name,
          temp: weatherData.main.temp,
          temp_max: weatherData.main.temp_max,
          temp_min: weatherData.main.temp_min,
          humidity: weatherData.main.humidity,
          wind: weatherData.wind.speed,
          country: weatherData.sys.country,
          description: weatherData.weather[0].description,
          icon: iconURL
        }
        this.setState({
          weatherInfo: weatherInfo
        })
      })
      .catch(err => alert("Wrong city name!"))
  }

  render() { 
    const { cityName, temp, temp_max, temp_min, humidity, wind, country, description, icon } = this.state.weatherInfo
    return (
      <Fragment>
        <AppTitle>Sophie's Weather</AppTitle>
        <InputWrapper>
          <SearchInput 
            value={this.state.inputValue} 
            placeholder='Enter the City Name' 
            onChange={this.inputChange}
          />
          <SearchButton onClick={this.searchWeather} marginBo="10px">Search</SearchButton>
        </InputWrapper>
        <OutputWrapper>
          <CityName>{cityName}</CityName>
          {country}
          {temp}
          {description}
          {temp_max}
          {temp_min}
          {humidity}
          {wind}
          <IconWrapper src={icon} />

        </OutputWrapper>
      </Fragment>
    );
  }
}

export default App;
