import React, { Component } from 'react';

class App extends Component {
  state = {
    inputValue: ''
  }

  inputChange = (e) => {
    this.setState({
      inputValue: e.target.value
    })
  }
  searchWeather = () => {
    const value = this.state.inputValue
    const APIkey = '638d3030eafa245040554df387be8a9c'
    const source = `api.openweathermap.org/data/2.5/weather?q=${value}&appid=${APIkey}`
    fetch(source)
      .then(response => response.json()
        .then(data => {
          console.log(data)
        }))
      .catch(error => {
        console.log(error)
      })
  }

  render() { 
    return (
      <div className="App">
        <input value={this.state.inputValue} placeholder='Enter the City Name' onChange={this.inputChange}/>
        <button onClick={this.searchWeather}>Search</button>
      </div>
    );
  }
}

export default App;
