import React, { Component, Fragment } from 'react';
import styled from 'styled-components'

const AppTitle = styled.h3`
  color: #ffffff;
  opacity: 1;
  height: auto;
  margin-top: 120px;
  font-size: 50px;
  text-align: center;
  transition: all .3s ease-in-out;

  &:hover {
    transform: scale(1.1);
  }
`
const InputWrapper = styled.div`
  text-align: center;
  margin-top: 60px;
  height: 50px;
`
const SearchInput = styled.input`
  font-size: 18px;
  width: 250px;
  border-radius: 5px;
  border: none;
  height: 50px;
  margin-right: 10px;
  &:focus {
    outline: none;
  }
  &::placeholder {
    font-size: 18px;
    height: 50px;
    line-height: 50px;
  }
`
const SearchButton = styled.button`
  height: 50px;
  width: 100px;
  line-height: 50px;
  border: none;
  border-radius: 5px;
  text-align: center;
  font-size: 18px;
  cursor: pointer;
  &:hover {
    opacity: 0.8;
  }
`
const OutputWrapper = styled.div`
  height: 800px;
  width: 500px;
  margin: 80px auto;
  color: #ffffff;
  text-align: center;
`
const CityName = styled.p`
  font-size: 80px;
  font-weight: 800;
  padding-bottom: 10px;
`
const Country = styled.p`
  font-size: 40px;
  font-weight: 800;
  padding-bottom: 30px;
`
const Temp = styled.p`
  font-size: 100px;
  font-weight:800;
  font-family: 'Merriweather', serif;
  padding-bottom: 20px;
`
const Description = styled.p`
  font-size: 30px;
`
const TempRange = styled.p`
  font-size: 20px;
  font-family: 'Merriweather', serif;
`
const IconWrapper = styled.img`
  width: 200px;
  height: 200px;
`

class App extends Component {
  state = {
    inputValue: '',
    weatherInfo: '',
    weatherDisplay: false //为了在142行加入摄氏度符号
  }

  inputChange = (e) => {
    this.setState({
      inputValue: e.target.value
    })
  }

  searchWeather = () => {
    const value = this.state.inputValue
    const APIkey = '638d3030eafa245040554df387be8a9c'
    const source = `https://api.openweathermap.org/data/2.5/weather?q=${value}&units=metric&appid=${APIkey}`
    fetch(source)
      .then(response => response.json())
      .then(weatherData => {
        const iconId = weatherData.weather[0].icon
        const iconURL = `http://openweathermap.org/img/wn/${iconId}@2x.png`
        console.log(weatherData)
        const weatherInfo = {
          cityName: weatherData.name,
          temp: weatherData.main.temp,
          temp_max: weatherData.main.temp_max,
          temp_min: weatherData.main.temp_min,
          country: weatherData.sys.country,
          description: weatherData.weather[0].description,
          icon: iconURL
        }
        this.setState({
          weatherInfo: weatherInfo,
          weatherDisplay: true
        })
      })
      .catch(err => alert("Wrong city name!"))
  }

  render() { 
    const { cityName, temp, temp_max, temp_min, country, description, icon } = this.state.weatherInfo
    const { weatherDisplay } = this.state.weatherDisplay
    return (
      <Fragment>
        <AppTitle>Sophie's Weather</AppTitle>
        <InputWrapper>
          <SearchInput 
            value={this.state.inputValue} 
            placeholder='Enter the City Name...' 
            onChange={this.inputChange}
          />
          <SearchButton onClick={this.searchWeather} marginBo="10px">Search</SearchButton>
        </InputWrapper>
        <OutputWrapper>
          <CityName>{cityName}</CityName>
          <Country>{country}</Country>
          {weatherDisplay && <Temp>{temp}°C</Temp>}
          <Description>{description}</Description>
          {weatherDisplay && <TempRange>{temp_min}°C ~ {temp_max}°C</TempRange>}
          {weatherDisplay && <IconWrapper src={icon} />}

        </OutputWrapper>
      </Fragment>
    );
  }
}

export default App;
