import React, { Component, Fragment } from 'react';
import styled from 'styled-components'


const InputWrapper = styled.div`
  text-align: center;
  margin-top: 100px;
`
const SearchInput = styled.input`
  font-size: 25px;
  border: ;
`
const SearchButton = styled.button`
  font-size: 25px;
`
const AppTitle = styled.h3`
  color: #ffffff;
  opacity: 1;
  height: auto;
  margin-top: 150px;
  font-size: 50px;
  text-align: center;
  transition: all .3s ease-in-out;

  &:hover {
    transform: scale(1.1);
  }
`



class App extends Component {
  state = {
    inputValue: ''
  }

  inputChange = (e) => {
    this.setState({
      inputValue: e.target.value
    })
  }
  searchWeather = () => {
    const value = this.state.inputValue
    const APIkey = '638d3030eafa245040554df387be8a9c'
    const source = `https://api.openweathermap.org/data/2.5/weather?q=${value}&appid=${APIkey}`
    fetch(source)
      .then(response => response.json())
      .then(weatherData => {
        console.log(weatherData)
      })
      .catch(err => alert("Wrong city name!"))
  }

  render() { 
    return (
      <>
        <AppTitle>Sophie's Weather</AppTitle>
        <InputWrapper>
          <SearchInput 
            value={this.state.inputValue} 
            placeholder='Enter the City Name' 
            onChange={this.inputChange}
          />
          <SearchButton onClick={this.searchWeather}>Search</SearchButton>
        </InputWrapper>
      </>
    );
  }
}

export default App;
