import React from 'react';
import styled from 'styled-components';
import ForecastDay from './ForecastDay'

const OutputWrapper = styled.div`
  margin: 60px auto;
  color: #ffffff;
  text-align: center;
`
const CityName = styled.p`
  font-size: 60px;
  font-weight: 800;
  padding-bottom: 10px;
`
const Date = styled.p`
  font-size: 30px;
  font-weight: 700;
`
const Temp = styled.p`
  font-size: 80px;
  font-weight:800;
  font-family: 'Merriweather', serif;
  padding-bottom: 30px;
`
const Description = styled.p`
  font-size: 30px;
  padding-bottom: 10px;
`
const TempRange = styled.p`
  font-size: 20px;
  font-family: 'Merriweather', serif;
`
const IconWrapper = styled.img`
  width: 100px;
  height: 100px;
`
const ForecastWrapper = styled.div`
  border-radius: 10px;
  flex-basis: 100%;
  margin: 10px 10px;
  background-color: rgba(300, 200, 400, 0.4);
  overflow: hidden;
`
const Forecast = styled.div`
  position: relative;
  display: flex;
  overflow-x: scroll;
  overflow-y: hidden;
  scrollbar-color: lightgray #ffffff;
  scrollbar-width: thin;
  margin-top: 20px;
  padding-bottom: 20px;
`
const Result = ({weather}) => {
  const{ cityName, date, temp, description, temp_min, temp_max, icon, forecastDaily}  = weather;

  const forecast = forecastDaily.map(item => (
    <ForecastDay
      key= {Math.random()}
      temp = {item.temp.day}
      icon = {item.weather[0].icon}
      main = {item.weather[0].main}
    />
  ))
  return (
    <>
      <OutputWrapper>
        <CityName>{cityName}</CityName>
        <Date>{date}</Date>
        {<Temp>{temp}°C</Temp>}
        <Description>{description}</Description>
        {<TempRange>{temp_min}°C ~ {temp_max}°C</TempRange>}
        {<IconWrapper src={icon} />}
      </OutputWrapper>
      <ForecastWrapper>
        <Forecast>{forecast}</Forecast>
      </ForecastWrapper>
    </>
  )
}

export default Result
