import React, { Component, Fragment } from 'react';
import styled from 'styled-components'

const AppTitle = styled.h3`
  color: #ffffff;
  opacity: 1;
  height: auto;
  margin-top: 100px;
  font-size: 50px;
  text-align: center;
  transition: all .3s ease-in-out;

  &:hover {
    transform: scale(1.1);
  }
`
const InputWrapper = styled.form`
  text-align: center;
  margin-top: 60px;
  height: 50px;
`
const SearchInput = styled.input`
  font-size: 18px;
  width: 250px;
  border-radius: 5px;
  border: none;
  height: 50px;
  margin-right: 10px;
  &:focus {
    outline: none;
  }
  &::placeholder {
    font-size: 18px;
    height: 50px;
    line-height: 50px;
  }
`
const SearchButton = styled.button`
  height: 50px;
  width: 100px;
  line-height: 50px;
  border: none;
  border-radius: 5px;
  text-align: center;
  font-size: 18px;
  cursor: pointer;
  &:hover {
    opacity: 0.8;
  }
`
const OutputWrapper = styled.div`
  height: 800px;
  width: 500px;
  margin: 80px auto;
  color: #ffffff;
  text-align: center;
`
const CityName = styled.p`
  font-size: 60px;
  font-weight: 800;
  padding-bottom: 10px;
`
const Temp = styled.p`
  font-size: 100px;
  font-weight:800;
  font-family: 'Merriweather', serif;
  padding-bottom: 30px;
`
const Description = styled.p`
  font-size: 30px;
  padding-bottom: 10px;
`
const TempRange = styled.p`
  font-size: 20px;
  font-family: 'Merriweather', serif;
`
const IconWrapper = styled.img`
  width: 200px;
  height: 200px;
`

class App extends Component {
  state = {
    inputValue: '',
    weatherInfo: '',
    weatherDisplay: false //为了在142行加入摄氏度符号
  }

  inputChange = (e) => {
    this.setState({
      inputValue: e.target.value
    })
  }

  searchWeather = (e) => {
    e.preventDefault(); //为了实现133行的onSubmit功能
    const value = this.state.inputValue
    const geoAPIkey = 'AIzaSyBjoZIOKg9rrHX3UXgMf1YmvBpfTGT7puM'
    const googleSource = `https://maps.googleapis.com/maps/api/geocode/json?address=${value}&key=${geoAPIkey}`
    fetch (googleSource)
      .then(response => response.json())
      .then(googleData => {
        console.log(googleData)
        const lat = googleData.results[0].geometry.location.lat
        const lng = googleData.results[0].geometry.location.lng
        console.log(lat, lng)

        const APIkey = '638d3030eafa245040554df387be8a9c'
        const weatherSource = `https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lng}&units=metric&appid=${APIkey}`
        fetch(weatherSource)
          .then(response => response.json())
          .then(weatherData => {
            console.log(weatherData)
            const iconId = weatherData.current.weather[0].icon
            const iconURL = `http://openweathermap.org/img/wn/${iconId}@2x.png`
            const weatherInfo = {
              cityName: weatherData.timezone,
              temp: weatherData.current.temp,
              temp_max: weatherData.daily[0].temp.max,
              temp_min: weatherData.daily[0].temp.min,
              description: weatherData.current.weather[0].description,
              icon: iconURL
            }
            this.setState({
              weatherInfo: weatherInfo,
              weatherDisplay: true
            })
          })
      })
      .catch(err =>console.log(err))
  }

  render() { 
    const { cityName, temp, temp_max, temp_min, description, icon } = this.state.weatherInfo
    const { weatherDisplay } = this.state
    return (
      <Fragment>
        <AppTitle>Sophie's Weather</AppTitle>
        <InputWrapper onSubmit={this.searchWeather}>
          <SearchInput 
            value={this.state.inputValue} 
            placeholder='Enter the City Name...' 
            onChange={this.inputChange}
          />
          <SearchButton onClick={this.searchWeather}>Search</SearchButton>
        </InputWrapper>
        <OutputWrapper>
          <CityName>{cityName}</CityName>
          {weatherDisplay && <Temp>{temp}°C</Temp>}
          <Description>{description}</Description>
          {weatherDisplay && <TempRange>{temp_min}°C ~ {temp_max}°C</TempRange>}
          {weatherDisplay && <IconWrapper src={icon} />}

        </OutputWrapper>
      </Fragment>
    );
  }
}

export default App;
