import React from 'react';
import styled from 'styled-components';

const OutputWrapper = styled.div`
  margin: 60px auto;
  color: #ffffff;
  text-align: center;
`
const CityName = styled.p`
  font-size: 60px;
  font-weight: 800;
  padding-bottom: 10px;
`
const Date = styled.p`
  font-size: 30px;
  font-weight: 700;
`
const Temp = styled.p`
  font-size: 80px;
  font-weight:800;
  font-family: 'Merriweather', serif;
  padding-bottom: 30px;
`
const Description = styled.p`
  font-size: 30px;
  padding-bottom: 10px;
`
const TempRange = styled.p`
  font-size: 20px;
  font-family: 'Merriweather', serif;
`
const IconWrapper = styled.img`
  width: 100px;
  height: 100px;
`

const Result = ({weather}) => {
  return (
    <OutputWrapper>
    <CityName>{cityName}</CityName>
    <Date>{date}</Date>
    {weatherDisplay && <Temp>{temp}°C</Temp>}
    <Description>{description}</Description>
    {weatherDisplay && <TempRange>{temp_min}°C ~ {temp_max}°C</TempRange>}
    {weatherDisplay && <IconWrapper src={icon} />}
    </OutputWrapper>
  )
}

export default Result
